def is_upper?(c)
    c >= 'A' && c <= 'Z'
end

def how_many_journalists(s)
    return s.length
end

def how_many_handles_numb(s)
    counter = 0
    s.each do |count|
        if count =~ /\d/
            counter += 1
        end
    end
    puts "#{counter} handles contiennent un numero."
end

def how_many_handles_aude(s)
    aude_counter = 0
    s.each do |count|
        if count =~ /aude/i
            aude_counter += 1
        end
    end
    puts "Le mot 'Aude' apparait #{aude_counter} fois dans cet array."
end

def how_many_handles_start_upper(s)
    first_upcase = 0
    s.each do |count|
        if is_upper?(count[1])
        first_upcase += 1
        end
    end
    puts "#{first_upcase} handles commencent par une majuscule."
end

def how_many_handles_have_upper(s)
    puts "#{s.count {|e| e =~ /[A-Z]/}} handles contiennent au moins une majuscule."
end

def how_many_underscores(s)
    count_under = 0
    s.each { |i| count_under += i.count("_")}
    puts "Il y a #{count_under} underscores dans tous les pseudos confondus."
end

def sort_alpha(s)
    ss = s.sort_by { |word| word.downcase }
    puts ss
end

def shortest(s)
    puts "Voici la liste des 50 handles les plus courts de cet array :"
    sss = s.sort_by { |word| word.length }
    50.times do |o|
        puts sss[o]
    end
end

def e_penser(s)
    puts "L'index d'e-penser dans cet array est le numero #{s.rindex('@epenser')}."
end

